import React from 'react'
import '../../static/site.css'

function ScrollingHeade() {
    window.addEventListener('scroll', function () {
        if (document.body.scrollTop > 50 || document.documentElement.scrollTop > 50) {
            document.documentElement.style.getElementsByClassName('scroll-header').getAttribute('font-size');
        } else {
            document.documentElement.style.getElementsByClassName('scroll-header').getAttribute('font-size').set('90px');
        }
    })
    return (
        <div className='scrolling-header'>
            Accounts of the Occult
        </div>
    )
}

export default ScrollingHeade
