import React, { Component } from 'react'
import '../../static/site.css'

class ScrollingHeade extends Component {
    constructor() {
        super();

        this.state = {
            fontSize: '90px'
        }
    };

    componentDidMount() { () => {
            window.addEventListener('scroll', this.getWindowHeight);
        };
    };

    componentWillUnmount() { () => {
            window.removeEventListener('scroll', this.getWindowHeight);
        };
    };

    getWindowHeight = () => {
        const distanceFromTop = window.pageYOffset || document.documentElement.scrollTop;
        const shrinkOn = '50px';
        if ( distanceFromTop > shrinkOn ) { 
            this.setState({
                fontSize: '30px'
            })
        } else {
            this.setState({
                fontSize: '90px'
            })
        }
    };
}

export default ScrollingHeade
